%angular
<div id="fps_total_report">
    <form name="searchTotalForm" id="searchTotalForm">
        <div class="form-group">
            <div class="row">
                <div class="col-xs-3 form-group">
                    <label for="settlementCycle">Settlement Cycle: </label>
                    <input type="text" class="form-control" id="settlementCycle" ng-model="data.settlementCycle"></input>
                </div>
            </div>
            <div class="row">
                <div class="col-xs-3 form-group">
                    <label for="startDate">Start Date: </label>
                    <input class="form-control" type="date" name="startDate" id="startDate" ng-model="data.startDate"></input>                    
                </div>
                <div class="col-xs-3 form-group">
                    <label for="endDate">End Date: </label>
                    <input class="form-control" type="date" name="endDate" id="endDate" ng-model="data.endDate"></input>
                </div>
            </div>
        </div>
        <button type="submit" class="btn btn-primary" ng-click="doQuery()"> Search</button>
    </form>
    <div class="clear-form-button">
        <button class="btn" ng-click="clearForm()">Clear Form</button>
    </div>
</div>
<script>

    angular.element(document).ready(function () {
        console.log('init');
        init_fps_total_report();
    });

    function init_fps_total_report(){

    console.log('init total report');
    var scope = angular.element($("#fps_total_report")).scope();

    scope.queryString = "";
    scope.data = {};

    scope.clearForm = function(){
        var scope = angular.element($("#fps_total_report")).scope();
        scope.data = {};
    }

    scope.doQuery = function(){
        var scope = angular.element($("#fps_total_report")).scope();
         var startDateQuery = "";
        var endDateQuery = "";
        if (scope.data.startDate != "" && scope.data.startDate != null) {
            var startDateElement = document.getElementById('startDate').value;            
            startDateQuery = "\'" + startDateElement + "\'";
        }
        if (scope.data.endDate != "" && scope.data.endDate != null) {
            var endDateElement = document.getElementById('endDate').value;
            endDateQuery = "\'" + endDateElement + "\'";

        }

        console.log("scope.data.startDate ",scope.data.startDate);
        console.log("scope.data.endDate ",scope.data.endDate);
        console.log("startDate ", startDate);
        console.log("endDate ", endDate);
        console.log("startDateQuery ", startDateQuery);
        console.log("endDateQuery ", endDateQuery);

        var values = [scope.data.settlementCycle, startDateQuery,  endDateQuery];

        var queryFields = ['"cycle_id" = ', '"date" >= ', '"date" <= '];

        var queryValues = queryFields.map(function(e, i) {
            var field = e;
            var value = values[i]
            if (value === null || typeof(value) == 'undefined' || value === "" || value == "'%%'"){
                return "";
            }
            if(field.includes("date")){
                value = "TO_TIMESTAMP("+value+")";            
            }else{
                value = "'"+value+"'";
            }        
            return e + value;
        });
        var notEmptyQueryValues = queryValues.filter(function check(c) { if (c === "") {return false} return true });

        var queryString = "";
        if (notEmptyQueryValues.length != 0){
            queryString = "AND " + notEmptyQueryValues.join(" and ");
        }
        scope.queryString = queryString;
        console.log('QUERY: ',scope.queryString);
        scope.z.angularBind('searchFPSTotalReportQuery', scope.queryString,'20181204-102500_1529756788');
        scope.z.runParagraph('20181204-102500_1529756788');
        scope.z.angularBind('searchFPSTotalReportQuery', scope.queryString,'20181204-150856_24987146');
        scope.z.runParagraph('20181204-150856_24987146');

        scope.z.angularBind('searchFPSTotalReportQuery', scope.queryString,'20181204-151315_475660801');
        scope.z.runParagraph('20181204-151315_475660801');

        scope.z.angularBind('searchFPSTotalReportQuery', scope.queryString,'20181204-151612_366524674');
        scope.z.runParagraph('20181204-151612_366524674');

        scope.z.angularBind('searchFPSTotalReportQuery', scope.queryString,'20181204-165650_1208989268');
        scope.z.runParagraph('20181204-165650_1208989268');

        scope.z.angularBind('searchFPSTotalReportQuery', scope.queryString,'20181226-160641_828852769');
        scope.z.runParagraph('20181226-160641_828852769');        



